mapper.select:
select 

from time_place
where course_id = #{courseId,jdbcType=INTEGER}
  and week_day = #{weekDay,jdbcType=INTEGER}
  and class_no = #{classNo,jdbcType=INTEGER}

mapper.delete:
delete from time_place
where course_id = #{courseId,jdbcType=INTEGER}
  and week_day = #{weekDay,jdbcType=INTEGER}
  and class_no = #{classNo,jdbcType=INTEGER}

mapper.update:
update time_place


    place = #{place,jdbcType=VARCHAR},


where course_id = #{courseId,jdbcType=INTEGER}
  and week_day = #{weekDay,jdbcType=INTEGER}
  and class_no = #{classNo,jdbcType=INTEGER}

update time_place
set place = #{place,jdbcType=VARCHAR}
where course_id = #{courseId,jdbcType=INTEGER}
  and week_day = #{weekDay,jdbcType=INTEGER}
  and class_no = #{classNo,jdbcType=INTEGER}
